#!/usr/bin/env python3
# -*- coding: utf-8 -*-

import struct
import base64

print(struct.pack('>I', 10240099))
print(struct.unpack('>IH', b'\xf0\xf0\xf0\xf0\x80\x80'))
print(struct.unpack('>ccc', b'\x61\x62\x63'))

bmp_data = base64.b64decode(
    '')


def bmp_info(data):
    bmp_struct = struct.unpack('<ccIIIIIIHH', data[:30])
    print(bmp_struct)
    if not (bmp_struct[0] == b'B' and bmp_struct[1] == b'M'):
        return

    return {
        'width': bmp_struct[6],
        'height': bmp_struct[7],
        'color': bmp_struct[9]
    }


# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')
